import React from "react";

function PeriodItemInput(props) {
  return (
    <>
      <input
        id={props.name.toLowerCase()}
        type="radio"
        name="period"
        checked={props.period === props.name}
        onChange={() => props.setAttribute("period", props.name)}
      />
      <label htmlFor={props.name.toLowerCase()}>{props.name}</label>
    </>
  );
}

function CreateAreaPeriod(props) {
  return (
    <div className="create-period">
      {["DAILY", "WEEKLY", "MONTHLY", "YEARLY"].map((x) => (
        <PeriodItemInput
          name={x}
          setAttribute={props.setAttribute}
          period={props.period}
        />
      ))}
      <input
        id="daily"
        type="radio"
        name="period"
        checked={props.period === "DAILY"}
        onChange={() => props.setAttribute("period", "DAILY")}
      />
      <label htmlFor="daily">DAILY</label>
      <input
        id="weekly"
        type="radio"
        name="period"
        checked={props.period === "WEEKLY"}
        onChange={() => props.setAttribute("period", "WEEKLY")}
      />
      <label htmlFor="weekly">WEEKLY</label>
      <input
        id="monthly"
        type="radio"
        name="period"
        checked={props.period === "MONTHLY"}
        onChange={() => props.setAttribute("period", "MONTHLY")}
      />
      <label htmlFor="monthly">MONTHLY</label>
      <input
        id="yearly"
        type="radio"
        name="period"
        checked={props.period === "YEARLY"}
        onChange={() => props.setAttribute("period", "YEARLY")}
      />
      <label htmlFor="yearly">YEARLY</label>
    </div>
  );
}

export default CreateAreaPeriod;
