import React from "react";

import CreateArea from "./CreateArea";
import Habit from "./Habit";
import Title from "./Title";

import { motion } from "framer-motion";

function App() {
  // Habit List
  const [crAreaExpanded, setCrAreaExpanded] = React.useState(false);
  const [habitList, setHabitList] = React.useState([
    {
      title: "WAKE UP EARLY",
      emoji: "ðŸŒž",
      count: 3,
      goal: 8,
      period: "WEEKLY",
      color: "#f8c51b",
    },
    {
      title: "WORKOUT",
      emoji: "ðŸ’ª",
      count: 4,
      goal: 5,
      period: "WEEKLY",
      color: "#f96262",
    },
    {
      title: "SAVE MONEY",
      emoji: "ðŸ’¸",
      count: 3,
      goal: 4,
      period: "DAILY",
      color: "#6ecfbb",
    },
  ]);

  // Add habit to list from CreateArea
  function addHabit(habitItem) {
    setHabitList((prevHabitList) => [...prevHabitList, habitItem]);
  }

  // Increase/Decrease count from Habit
  function setCount(id, newCount) {
    if (newCount < 0) return;
    setHabitList((prevHabitList) =>
      prevHabitList.map((item, index) => {
        return index === id ? { ...item, count: newCount } : item;
      })
    );
  }

  return (
    <div>
      <Title />
      <motion.div>
        <CreateArea
          addHabit={addHabit}
          isExpanded={crAreaExpanded}
          setExpanded={setCrAreaExpanded}
        />
      </motion.div>

      {habitList.map((habitItem, index) => (
        <Habit
          key={index}
          id={index}
          title={habitItem.title}
          emoji={habitItem.emoji}
          count={habitItem.count}
          goal={habitItem.goal}
          period={habitItem.period}
          color={habitItem.color}
          setCount={setCount}
        />
      ))}
    </div>
  );
}

export default App;
