import React from "react";

function CreateArea(props) {
  const [newHabit, setNewHabit] = React.useState({
    title: "",
    count: 0,
    goal: 5,
    period: "WEEKLY",
  });

  function handleChange(ev) {
    const { name, value } = ev.target;
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, [name]: value }));
  }

  function handleClick(ev) {
    props.addHabit(newHabit);
    setNewHabit({
      title: "",
      count: 0,
      goal: 5,
      period: "WEEKLY",
    });
    ev.preventDefault();
  }

  return (
    <div className="create-area">
      <form>
        <input
          className="create-title"
          name="title"
          placeholder="GOAL"
          onChange={handleChange}
          value={newHabit.title}
        />
        {/* TODO: REMOVE FROM HERE AND CSS */}
        <input
          className="create-description"
          name="period"
          placeholder="Enter period"
          onChange={handleChange}
          value={newHabit.period}
        />

        <input id="daily" type="radio" name="period" />
        <label for="daily">Daily</label>
        <input id="weekly" type="radio" name="period" />
        <label for="weekly">Weekly</label>
        <input id="monthly" type="radio" name="period" />
        <label for="monthly">Monthly</label>
        <input id="yearly" type="radio" name="period" />
        <label for="yearly">Yearly</label>

        {/* Daily
        </input>
        <input type="radio" name="period">
          Weekly
        </input>
        <input type="radio" name="period">
          Monthly
        </input>
        <input type="radio" name="period">
          Yearly
        </input> */}

        <button type="submit" onClick={handleClick}>
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
