import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreateArea from "./CreateArea";
import Habit from "./Habit";
import Title from "./Title";

function App() {
  const [habitList, setHabitList] = React.useState([
    {
      title: "WAKE UP EARLY",
      description: "WEEKLY: 03/03 MIN",
      count: 0,
    },
    {
      title: "WAKE UP EARLY",
      description: "WEEKLY: 03/03 MIN",
      count: 0,
    },
  ]);

  function addHabit(habitItem) {
    setHabitList((prevHabitList) => [...prevHabitList, habitItem]);
  }

  return (
    <div>
      <Header />
      <Title />
      <CreateArea addHabit={addHabit} />
      {habitList.map((habitItem) => (
        <Habit
          key={1}
          id={1}
          title={habitItem.title}
          description={habitItem.description}
          count={habitItem.count}
        />
      ))}
      <Footer />
    </div>
  );
}

export default App;
