import React from "react";

function CreateAreaGoal(props) {
  const periodString =
    props.period === "DAILY" ? "DAY" : props.period.slice(0, -1);

  return (
    <div className="create-goal">
      <input
        type="number"
        value={props.goal}
        onChange={() => props.setGoal(props.goal + 1)}
      />
      <p>TIMES PER {periodString}</p>
    </div>
  );
}

export default CreateAreaGoal;
