import React from "react";

import CreateAreaPeriod from "./CreateAreaPeriod";
import CreateAreaGoal from "./CreateAreaGoal";
import CreateAreaColor from "./CreateAreaColor";

import Picker from "emoji-picker-react";

import AddIcon from "@material-ui/icons/Add";
import ClearIcon from "@material-ui/icons/Close";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";
import Collapse from "@material-ui/core/Collapse";

function CreateArea(props) {
  const [newHabit, setNewHabit] = React.useState({
    title: "",
    count: 0,
    goal: 3,
    period: "DAILY",
    color: "#f8c51b",
  });
  const [isExpanded, setExpanded] = React.useState(false);

  // Handle input changes
  function handleTitleChange(ev) {
    let { name, value } = ev.target;
    if (typeof value === "string") value = value.toUpperCase();
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, [name]: value }));
  }
  // TODO: CLEAN THIS CODE UP
  // Handle input changes from CreateAreaPeriod
  function setPeriod(newPeriod) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, period: newPeriod }));
  }
  // Handle input changes from CreateAreaGold
  function setGoal(newGoal) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, goal: newGoal }));
  }
  function setColor(newColor) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, color: newColor }));
  }

  // Handle add button click
  function handleClick(ev) {
    if (newHabit.title !== "" && newHabit.goal > 0) {
      props.addHabit(newHabit);
      setNewHabit({
        title: "",
        count: 0,
        goal: 5,
        period: "DAILY",
        color: "#f8c51b",
      });
    }
    ev.preventDefault();
  }

  return (
    <div
      className="create-area"
      style={{
        backgroundColor: newHabit.color,
      }}
      onClick={() => {
        if (!isExpanded) setExpanded(true);
      }}
    >
      <h2>CREATE</h2>
      <Collapse in={isExpanded}>
        <form>
          {/* Create Title */}
          <div className="create-group">
            <p>NAME YOUR HABIT</p>
            <input
              className="create-title"
              name="title"
              placeholder="Enter your habit"
              onChange={handleTitleChange}
              value={newHabit.title}
            />
          </div>

          {/* Create Emoji */}
          <div className="create-group">
            <p>SET AN EMOJI</p>
            <Picker onEmojiClick={onEmojiClick} />
          </div>
          {/* Create Period */}
          <div className="create-group">
            <p>CHOOSE A PERIOD</p>
            <CreateAreaPeriod period={newHabit.period} setPeriod={setPeriod} />
          </div>

          {/* Create Goal */}
          <div className="create-group">
            <p>SET YOUR GOAL</p>
            <CreateAreaGoal
              period={newHabit.period}
              goal={newHabit.goal}
              setGoal={setGoal}
            />
          </div>

          {/* Create Color */}
          <div className="create-group">
            <p>CUSTOMISE THE COLOUR</p>
            <CreateAreaColor color={newHabit.color} setColor={setColor} />
          </div>

          {/* Add/Cancel Buttons - Material UI Package */}
          <Zoom in={isExpanded}>
            <Fab className="add-habit-icon" type="submit" onClick={handleClick}>
              <AddIcon />
            </Fab>
          </Zoom>
          <Zoom in={isExpanded}>
            <Fab
              className="clear-habit-icon"
              onClick={() => setExpanded(false)}
            >
              <ClearIcon />
            </Fab>
          </Zoom>
        </form>
      </Collapse>
    </div>
  );
}

export default CreateArea;
