import React from "react";
import CreateAreaPeriod from "./CreateAreaPeriod";
import CreateAreaGoal from "./CreateAreaGoal";
import CreateAreaColor from "./CreateAreaColor";

function CreateArea(props) {
  const [newHabit, setNewHabit] = React.useState({
    title: "",
    count: 0,
    goal: 5,
    period: "DAILY",
    color: "#f8c51b",
  });

  // Handle input changes
  function handleTitleChange(ev) {
    let { name, value } = ev.target;
    if (typeof value === "string") value = value.toUpperCase();
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, [name]: value }));
  }
  // Handle input changes from CreateAreaPeriod
  function setPeriod(newPeriod) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, period: newPeriod }));
  }
  // Handle input changes from CreateAreaGold
  function setGoal(newGoal) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, goal: newGoal }));
  }
  function setColor(newColor) {
    setNewHabit((prevNewHabit) => ({ ...prevNewHabit, color: newColor }));
  }

  // Handle add button click
  function handleClick(ev) {
    if (newHabit.title !== "" && newHabit.goal > 0) {
      props.addHabit(newHabit);
      setNewHabit({
        title: "",
        count: 0,
        goal: 5,
        period: "DAILY",
        color: "#f8c51b",
      });
    }
    ev.preventDefault();
  }

  return (
    <div className="create-area">
      <form>
        <div className="create-group">
          <p>NAME YOUR HABIT :</p>
          <input
            className="create-title"
            name="title"
            placeholder="ADD A NEW HABIT"
            onChange={handleTitleChange}
            value={newHabit.title}
          />
        </div>
        <div className="create-group">
          <p>CHOOSE A PERIOD :</p>
          <CreateAreaPeriod period={newHabit.period} setPeriod={setPeriod} />
        </div>
        <div className="create-group">
          <p>SET YOUR GOAL :</p>
          <CreateAreaGoal
            period={newHabit.period}
            goal={newHabit.goal}
            setGoal={setGoal}
          />
        </div>
        <div className="create-group">
          <p>CUSTOMISE THE COLOUR :</p>
          <CreateAreaColor color={newHabit.color} setColor={setColor} />
        </div>

        <button type="submit" onClick={handleClick}>
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
