import React from "react";
import ExpandLessIcon from "@material-ui/icons/ExpandLess";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

import { motion, useAnimation } from "framer-motion";
import Collapse from "@material-ui/core/Collapse";

function Habit(props) {
  const controls = useAnimation();
  const [isExpanded, setExpanded] = React.useState(false);
  // const [widthPercent, setWidthPercent] = React.useState(0);
  // let innerTimer = 0;
  // React.useEffect(() => {
  //   // Set time() once when component rendered
  //   time();
  // }, []);

  // console.log(widthPercent);
  // function time() {
  //   // PROBLEM: innerTimer used as widthPercent was not working, widthPercent would adjust outside time() but remain at 0 inside time()
  //   console.log(innerTimer);
  //   if (innerTimer < Math.round((100 * props.count) / props.goal)) {
  //     setWidthPercent(() => ++innerTimer);
  //     console.log(innerTimer);
  //     let id = setTimeout(time, 10);
  //   }

  //       style={{
  // background: `linear-gradient(to right, #f8c51b
  //   ${widthPercent}%,
  //   #dedede ${widthPercent}%)`,
  //   }}
  // }

  return (
    <div className="habit">
      <motion.div
        // Framer-motion
        animate={controls}
        onClick={() => setExpanded(!isExpanded)}
        onMouseDown={() => controls.start({ scale: 1.1 })}
        onMouseUp={() => controls.start({ scale: 1 })}
        onMouseOut={() => controls.start({ scale: 1 })}
        onMouseOver={() => controls.start({ scale: 1.05 })}
        // React
        className="habit-tile"
        style={{
          background: `linear-gradient(to right, ${props.color}
        ${Math.round((100 * props.count) / props.goal)}%,
        #dedede ${Math.round((100 * props.count) / props.goal)}%)`,
        }}
      >
        {/* Habit Title and Description */}
        <div className="habit-text">
          <h2>{props.title + " " + props.emoji}</h2>
          <p>{`${props.period}: ${
            props.count < 10 ? "0" + props.count : props.count
          } / ${props.goal < 10 ? "0" + props.goal : props.goal} COMPLETE`}</p>
        </div>

        {/* Habit Counter and Increase/Decrease Buttons */}
        <div className="habit-counter">
          <p>{props.count}</p>
          <div
            className="habit-increase-decrease"
            onMouseDown={(ev) => ev.stopPropagation()}
            onMouseUp={(ev) => ev.stopPropagation()}
            onMouseOut={(ev) => ev.stopPropagation()}
          >
            <ExpandLessIcon
              className="count-button"
              onClick={(ev) => {
                props.setCount(props.id, props.count + 1);
                if (props.count + 1 === props.goal) {
                  controls.start({
                    x: [0, 400, -400, 0],
                    opacity: [1, 0, 0, 1],
                  });
                }
                ev.stopPropagation();
                // time();
              }}
            />
            <ExpandMoreIcon
              className="count-button"
              onClick={(ev) => {
                props.setCount(props.id, props.count - 1);
                ev.stopPropagation();
              }}
            />
          </div>
        </div>
      </motion.div>

      <Collapse in={isExpanded}>
        <div
          className="habit-information"
          style={{
            backgroundColor: "#dedede",
          }}
        >
          <p>
            You have completed <strong>{props.count}</strong> in total!
          </p>
          <br></br>
          <p>
            Every{" "}
            <strong>
              {props.period === "DAILY"
                ? "day"
                : props.period.slice(0, -2).toLowerCase()}
            </strong>{" "}
            you have made a commitment to{" "}
            <strong>{props.title.toLowerCase()}</strong> at least{" "}
            <strong>{props.goal}</strong> times. For this{" "}
            {props.period === "DAILY"
              ? "day"
              : props.period.slice(0, -2).toLowerCase()}{" "}
            you have {props.goal - props.count} more remaining.
          </p>
          <textarea placeholder="Write a note..." rows={3} />
        </div>
      </Collapse>
    </div>
  );
}

export default Habit;
