import React from "react";
import { motion, useAnimation } from "framer-motion";

function ColorItemInput(props) {
  const controls = useAnimation();
  return (
    <motion.input
      id={props.colorItemColor}
      type="radio"
      name="color"
      checked={props.color === props.colorItemColor}
      onChange={() => props.setColor(props.colorItemColor)}
      animate={controls}
      onMouseDown={() => controls.start({ scale: 0.9 })}
      onMouseUp={() => controls.start({ scale: 1 })}
      onMouseOut={() => controls.start({ scale: 1 })}
    />
  );
}
function ColorItemLabel(props) {
  const controls = useAnimation();
  return (
    <label
      htmlFor={props.colorItemColor}
      style={{
        backgroundColor: props.colorItemColor,
      }}
      animate={controls}
      onMouseDown={() => controls.start({ scale: 0.9 })}
      onMouseUp={() => controls.start({ scale: 1 })}
      onMouseOut={() => controls.start({ scale: 1 })}
    ></label>
  );
}

function CreateAreaColor(props) {
  return (
    <div className="create-color">
      {/* Yellow */}
      <ColorItemInput
        colorItemColor="#f8c51b"
        color={props.color}
        setColor={props.setColor}
      />
      <ColorItemLabel colorItemColor="#f8c51b" />

      {/* Red */}
      <ColorItemInput
        colorItemColor="#f96262"
        color={props.color}
        setColor={props.setColor}
      />
      <ColorItemLabel colorItemColor="#f96262" />

      {/* LightBlue */}
      <ColorItemInput
        colorItemColor="#23d5e7"
        color={props.color}
        setColor={props.setColor}
      />
      <ColorItemLabel colorItemColor="#23d5e7" />
      {/* Purple */}
      <ColorItemInput
        colorItemColor="#dda6F7"
        color={props.color}
        setColor={props.setColor}
      />
      <ColorItemLabel colorItemColor="#dda6F7" />
      {/* DarkBlue */}
      <ColorItemInput
        colorItemColor="#2198d3"
        color={props.color}
        setColor={props.setColor}
      />
      <ColorItemLabel colorItemColor="#2198d3" />
    </div>
  );
}

export default CreateAreaColor;
