import React from "react";

import CreateArea from "./CreateArea";
import Habit from "./Habit";
import Title from "./Title";

import { motion } from "framer-motion";

function App() {
  // Habit List
  const [habitList, setHabitList] = React.useState([
    {
      title: "WAKE UP EARLY",
      count: 3,
      goal: 10,
      period: "WEEKLY",
      color: "#f8c51b",
    },
    {
      title: "WAKE UP EARLY",
      count: 4,
      goal: 5,
      period: "WEEKLY",
      color: "#f96262",
    },
  ]);

  // Add habit to list from CreateArea
  function addHabit(habitItem) {
    setHabitList((prevHabitList) => [...prevHabitList, habitItem]);
  }

  // Increase/Decrease count from Habit
  function setCount(id, newCount) {
    if (newCount < 0) return;
    setHabitList((prevHabitList) =>
      prevHabitList.map((item, index) => {
        return index === id ? { ...item, count: newCount } : item;
      })
    );
  }

  return (
    <div>
      <Title />
      <motion.div>
        <CreateArea addHabit={addHabit} />
      </motion.div>

      {habitList.map((habitItem, index) => (
        <Habit
          key={index}
          id={index}
          title={habitItem.title}
          count={habitItem.count}
          goal={habitItem.goal}
          period={habitItem.period}
          setCount={setCount}
        />
      ))}
    </div>
  );
}

export default App;
