import React from "react";
import ExpandLessIcon from "@material-ui/icons/ExpandLess";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

import { motion } from "framer-motion";

function Habit(props) {
  const [isTapped, setTapped] = React.useState(false);

  return (
    <motion.div
      animate={{ scale: 0.9 }}
      className="habit"
      style={{
        background: `linear-gradient(to right, #f8c51b 
          ${Math.round(
            (100 * props.count) / props.goal
          )}%, #dedede ${Math.round((100 * props.count) / props.goal)}%)`,
      }}
    >
      {/* Habit Title and Description */}
      <div className="habit-text" onClick={() => setTapped(true)}>
        <h2>{props.title}</h2>
        <p>{`${props.period} GOAL: ${props.count} / ${props.goal} COMPLETE`}</p>
      </div>

      {/* Habit Counter and Increase/Decrease Buttons */}
      <div className="habit-counter">
        <p>{props.count}</p>
        <div className="habit-increase-decrease">
          <ExpandLessIcon
            className="count-button"
            onClick={() => {
              props.setCount(props.id, props.count + 1);
            }}
          />
          <ExpandMoreIcon
            className="count-button"
            onClick={() => {
              props.setCount(props.id, props.count - 1);
            }}
          />
        </div>
      </div>
    </motion.div>
  );
}

export default Habit;
